pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC721/ERC721Full.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721Burnable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts/GSN/Context.sol";

contract HashtagProtocol is Ownable, ERC721Full, ERC721Burnable {
    using SafeMath for uint256;

    event MintHashtag(uint256 indexed tokenId, address indexed owner, string hashtag, address indexed publisher);

    event AddPublisher(address indexed publisher);
    event RemovePublisher(address indexed publisher);

    uint256 public tokenPointer = 0;
    uint256 public fee = 0.01 ether;

    mapping(uint256 => string) public tokenIdToHashtag;
    mapping(string => uint256) public hashtagToTokenId;

    mapping(uint256 => address) public tokenIdToPublisher;
    mapping(uint256 => uint256) public tokenIdToCreatedDate;

    mapping(address => bool) public publisherWhitelist;

    address payable public platform;
    uint256 public platformPercentage = 90;

    constructor (address payable _platform) public ERC721Full("Hashtag Protocol", "HASHTAG") {
        platform = _platform;
    }

    function exists(uint256 tokenId) public view returns (bool) {
        return _exists(tokenId);
    }

    function tokensOfOwner(address owner) public view returns (uint256[] memory) {
        return _tokensOfOwner(owner);
    }

    function setTokenURI(uint256 tokenId, string memory uri) onlyOwner public {
        _setTokenURI(tokenId, uri);
    }

    function setBaseURI(string memory baseURI) onlyOwner public {
        _setBaseURI(baseURI);
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal virtual override(ERC721, ERC721Full) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function mint(string memory hashtag, address payable publisher) payable public {

        require(msg.value >= fee || isOwner(), "Mint: You must send at least the fee amount or be owner.");

        require(publisherWhitelist[publisher] == true, "Mint: You must whitelisted to publish.");

        string memory lowerHashtag = lower(hashtag);
        require(hashtagToTokenId[lowerHashtag] == 0, "Mint: Hashtag already owned.");

        tokenPointer = tokenPointer.add(1);
        uint256 tokenId = tokenPointer;

        tokenIdToHashtag[tokenId] = lowerHashtag;
        hashtagToTokenId[lowerHashtag] = tokenPointer;

        tokenIdToPublisher[tokenId] = publisher;
        tokenIdToCreatedDate[tokenId] = block.timestamp;

        _mint(msg.sender, tokenId);

        // split fee
        uint256 platformPayment = msg.value.div(100).mul(platformPercentage);
        platform.transfer(platformPayment);
        publisher.transfer(msg.value.sub(platformPayment));

        emit MintHashtag(tokenId, msg.sender, lowerHashtag, publisher);
    }

    function setFee(uint256 _fee) onlyOwner external {
        fee = _fee;
    }

    function setPlatformPercentage(uint256 _platformPercentage) onlyOwner external {
        platformPercentage = _platformPercentage;
    }

    function setPlatform(address payable _platform) onlyOwner external {
        platform = _platform;
    }

    function addPublisher(address _publisher) onlyOwner external {
        publisherWhitelist[_publisher] = true;

        emit AddPublisher(_publisher);
    }

    function removePublisher(address _publisher) onlyOwner external {
        publisherWhitelist[_publisher] = false;

        emit RemovePublisher(_publisher);
    }

    // helper functions
    function lower(string memory _base) internal pure returns (string memory) {
        bytes memory _baseBytes = bytes(_base);
        for (uint i = 0; i < _baseBytes.length; i++) {
            _baseBytes[i] = _lower(_baseBytes[i]);
        }
        return string(_baseBytes);
    }

    function _lower(bytes1 _b1) private pure returns (bytes1) {

        if (_b1 >= 0x41 && _b1 <= 0x5A) {
            return bytes1(uint8(_b1) + 32);
        }

        return _b1;
    }
}
