pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC721/ERC721Full.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721Burnable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./HashtagAccessControls.sol";

/**
 * @title HashtagProtocol contract
 * @notice Core smart contract of the protocol that governs the creation of hashtag tokens
 * @author Hashtag Protocol
*/
contract HashtagProtocol is ERC721Full, ERC721Burnable {
    using SafeMath for uint256;

    event MintHashtag(uint256 indexed tokenId, address indexed owner, string hashtag, address indexed publisher);

    // Definition of a Hashtag which bundles associated metadata
    struct Hashtag {
        uint256 created;
        address originalPublisher;
        address creator;
        string value;
    }

    uint256 public tokenPointer = 0;
    uint256 public fee = 0.01 ether;

    mapping(uint256 => Hashtag) public tokenIdToHashtag;
    mapping(string => uint256) public hashtagToTokenId;

    address payable public platform;
    uint256 public platformPercentage = 9000;
    uint256 modulo = 10000;

    HashtagAccessControls public accessControls;

    /**
     * @notice Admin only execution guard
     * @dev When applied to a method, only allows execution when the sender has the admin role
    */
    modifier onlyAdmin() {
        require(isAdmin(_msgSender()), "Caller must be admin");
        _;
    }

    constructor (HashtagAccessControls _accessControls, address payable _platform)
    public ERC721Full("Hashtag Protocol", "HASHTAG") {
        accessControls = _accessControls;
        platform = _platform;
    }

    /**
     * @notice Utility method for checking whether a hashtag exists
     * @param _tokenId ID of the hashtag
     * @return bool True if a hashtag exists, false if not
    */
    function exists(uint256 _tokenId) public view returns (bool) {
        return _exists(_tokenId);
    }

    /**
     * @notice Utility method for retrieving all hashtags of a given address
     * @param _owner Address of a hashtag token owner
     * @return uint256[] List of IDs owned by the above address
    */
    function tokensOfOwner(address _owner) public view returns (uint256[] memory) {
        return _tokensOfOwner(_owner);
    }

    /**
     * @notice Admin method for updating the token URI of a hashtag
     * @param _tokenId ID of the hashtag
     * @param _uri New token URI
    */
    function setTokenURI(uint256 _tokenId, string memory _uri) onlyAdmin public {
        _setTokenURI(_tokenId, _uri);
    }

    /**
     * @notice Admin method for updating the base token URI of a hashtag
     * @param _baseURI Base URI for all tokens
    */
    function setBaseURI(string memory _baseURI) onlyAdmin public {
        _setBaseURI(_baseURI);
    }

    function _beforeTokenTransfer(address _from, address _to, uint256 _tokenId) internal virtual override(ERC721, ERC721Full) {
        super._beforeTokenTransfer(_from, _to, _tokenId);
    }

    /**
     * @notice Method that a publisher can call to create a hashtag
     * @dev A fee is required unless the caller has an admin role
     * @param _hashtag String version of the hashtag to mint
     * @param _publisher Address of the publisher through which the hashtag is being created
     * @param _recipient Address that will receive the hashtag
     * @return _tokenId ID of the new hashtag
    */
    function mint(string memory _hashtag, address payable _publisher, address _recipient) payable public returns (uint256 _tokenId) {
        require(_senderCanMintForPublisher(_publisher), "Mint: You must be either admin or be authorised and have paid the platform fee");

        // generate the lowercase version of the hashtag and ensure it's not already been minted
        string memory lowerHashtag = lower(_hashtag);
        require(hashtagToTokenId[lowerHashtag] == 0, "Mint: Hashtag already owned.");

        // generate the new hashtag token id
        tokenPointer = tokenPointer.add(1);
        uint256 tokenId = tokenPointer;

        // create the hashtag
        tokenIdToHashtag[tokenId] = Hashtag({
            value: lowerHashtag,
            created: now,
            originalPublisher: _publisher,
            creator: _msgSender()
        });

        // store a reverse lookup and mint the tag
        hashtagToTokenId[lowerHashtag] = tokenPointer;

        _mint(_recipient, tokenId);

        // split fee
        uint256 platformPayment = fee.div(modulo).mul(platformPercentage);
        (bool platformSuccess,) = platform.call{value: platformPayment}("");
        require(platformSuccess, "Failed to transfer commission to platform");

        (bool publisherSuccess, ) = _publisher.call{value: msg.value.sub(platformPayment)}("");
        require(publisherSuccess, "Failed to transfer commission to publisher");

        // send the user any change back
        _refundRemainingBalance();

        // log the minting event
        emit MintHashtag(tokenId, msg.sender, lowerHashtag, _publisher);

        return tokenId;
    }

    /**
     * @notice Admin functionality for setting the fee required in order to create and mint a hashtag
     * @param _fee Value that the fee will be set to in WEI
    */
    function setFee(uint256 _fee) onlyAdmin external {
        fee = _fee;
    }

    /**
     * @notice Admin functionality for updating the percentage of the minting fee that the platform receives
     * @dev The percentage defined must be to 2 decimal places. For example: For a percentage of 12.55%, multiply this by 100 to get 1255
     * @param _platformPercentage Percentage of the minting fee that the Hashtag Protocol receives
    */
    function setPlatformPercentage(uint256 _platformPercentage) onlyAdmin external {
        platformPercentage = _platformPercentage;
    }

    /**
     * @notice Admin functionality for updating the address that receives the commission on behalf of the platform
     * @param _platform Address that receives platform fees
    */
    function setPlatform(address payable _platform) onlyAdmin external {
        platform = _platform;
    }

    /**
     * @notice Returns the commission addresses related to a token
     * @param _tokenId ID of a hashtag
     * @return _platform Platform commission address
     * @return _owner Address of the owner of the hashtag
    */
    function getPaymentAddresses(uint256 _tokenId) public view returns (
        address payable _platform,
        address payable _owner
    ) {
        return (
            platform,
            payable(ownerOf(_tokenId))
        );
    }

    /**
     * @notice Checks whether an address has an admin role
     * @param _addr Address being checked
     * @return bool True if the address has the role, false if not
    */
    function isAdmin(address _addr) public view returns (bool) {
        bytes32 adminRole = 0x00;
        return accessControls.hasRole(adminRole, _addr);
    }

    /**
     * @notice Checks whether an address has a publisher role
     * @param _addr Address being checked
     * @return bool True if the address has the role, false if not
    */
    function isPublisher(address _addr) public view returns (bool) {
        return accessControls.hasRole(keccak256("PUBLISHER"), _addr);
    }

    /**
     * @notice Checks whether an address has the minter role
     * @param _addr Address being checked
     * @return bool True if the address has the role, false if not
    */
    function isMinter(address _addr) public view returns (bool) {
        return accessControls.hasRole(keccak256("HASHTAG_MINTER_ROLE"), _addr);
    }

    /**
     * @notice Checks whether an address and the sender match and whether they have the publisher role
     * @param _addr Address being checked
     * @return bool True if the sender has the role, false if not
    */
    function isSenderPublisher(address _addr) public view returns (bool) {
        //require(_addr == _msgSender(), "Address supplied as publisher and sender do not match");
        return isPublisher(_addr);
    }

    /**
     * @notice A sender can mint when they are either admin or are an authorised sender and have paid the platform fee
     * @param _publisher Address assumed to be a publisher
     * @return bool True if the sender can mint for a given publisher, false if they are either not a publisher or have not paid the minting fee
    */
    function _senderCanMintForPublisher(address _publisher) private view returns (bool) {
        bool isSenderAdmin = isAdmin(_msgSender());
        bool isAuthorisedSender = isMinter(_msgSender()) || isSenderPublisher(_publisher);
        return isSenderAdmin || (isAuthorisedSender && msg.value >= fee);
    }

    /**
     * @notice Converts a string to its lowercase equivalent
     * @param _base String to convert
     * @return string Lowercase version of string supplied
    */
    function lower(string memory _base) internal pure returns (string memory) {
        bytes memory _baseBytes = bytes(_base);
        for (uint i = 0; i < _baseBytes.length; i++) {
            _baseBytes[i] = _lower(_baseBytes[i]);
        }
        return string(_baseBytes);
    }

    /**
     * @notice Given a contract balance, sends the value back to the sender - the equivalent of giving a user change
    */
    function _refundRemainingBalance() private {
        address payable self = payable(address(this));
        (bool refundSuccess, ) = _msgSender().call{value: self.balance}("");
        require(refundSuccess, "Failed to refund the remaining balance to sender");
    }

    /**
     * @notice Internal method that converts a character of a string to the lowercase equivalent
     * @param _b1 byte representation of a character from a sting
     * @return bytes1 lower case version of a character from a string as bytes1
    */
    function _lower(bytes1 _b1) private pure returns (bytes1) {

        if (_b1 >= 0x41 && _b1 <= 0x5A) {
            return bytes1(uint8(_b1) + 32);
        }

        return _b1;
    }
}

